declare const _default: {
    name: string;
    meta: {
        type: "suggestion" | "problem" | "layout";
        messages: {
            useIsNullObject: string;
        };
        docs: {
            description: string;
            category: "Best Practices" | "Stylistic Issues" | "Variables" | "Possible Errors";
            recommended: false | "error" | "warn";
            url: string;
        };
        schema: never[];
        fixable: "code" | "whitespace";
    };
    create: (context: any) => {
        "Program:exit"(): void;
    };
};
export = _default;
